-- SQL script to create and populate the Optica Amazonas database
CREATE DATABASE optica_amazonas;

-- Connect to the newly created database
\c optica_amazonas;

CREATE TABLE clientes (
    client_id INT PRIMARY KEY,
    name VARCHAR(100) NOT NULL,
    gender VARCHAR(20),
    birthdate DATE,
    signup_date DATE,
    city VARCHAR(100)
);

CREATE TABLE produtos (
    product_id INT PRIMARY KEY,
    name VARCHAR(100) NOT NULL,
    category VARCHAR(50),
    base_price NUMERIC(10,2)
);

CREATE TABLE campanhas (
    campaign_id INT PRIMARY KEY,
    name VARCHAR(100) NOT NULL,
    type VARCHAR(20),
    start_date DATE,
    end_date DATE,
    description TEXT
);

CREATE TABLE vendas (
    transaction_id BIGINT PRIMARY KEY,
    date DATE,
    client_id INT REFERENCES clientes(client_id),
    product_id INT REFERENCES produtos(product_id),
    quantity INT,
    unit_price NUMERIC(10,2),
    total_price NUMERIC(10,2),
    discount NUMERIC(10,2),
    final_price NUMERIC(10,2),
    campaign_id INT REFERENCES campanhas(campaign_id)
);

